package my_programms;

public class If_else_and {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a,b,c;
		a=4000;
		b=40;
		c=500;
		
		
		if (a>b && a>c)
		{
			System.out.println("a>b and c");
		}
		else if (b>a)
		{
			System.out.println("b>a and c");
		}
		
		else
		
		{

		System.out.println("c>a and b");	
		}	
	}
			
}
	


